<?phpclass SiteController extends Controller {    public $layout = '';    /**     * Declares class-based actions.     */    public function actions() {        return array(            // captcha action renders the CAPTCHA image displayed on the contact page            'captcha' => array(                'class' => 'CCaptchaAction',                'backColor' => 0xFFFFFF,            ),            // page action renders "static" pages stored under 'protected/views/site/pages'            // They can be accessed via: index.php?r=site/page&view=FileName            'page' => array(                'class' => 'CViewAction',            ),        );    }    /**     * This is the action to handle external exceptions.     */    public function actionError() {        if ($error = Yii::app()->errorHandler->error) {            if (Yii::app()->request->isAjaxRequest)                echo $error['message'];            else                $this->render('error', $error);        }    }    public function actionFacebook_Oauth() {        $this->render('facebook_oauth', array(        ));    }    public function actionGoogle_Oauth() {        $this->render('google_oauth', array(        ));    }    public function actionFLogout() {        $params = array('next' => $_GET['url']);        $logout_url = Yii::app()->facebook->getLogoutUrl($params);        Yii::app()->facebook->destroySession();        session_destroy();//        echo $logout_url;        $this->redirect($logout_url);    }}